import { useState } from "react";

const UserInput=()=>{

    const [userInput,setUserInput]=useState({
        'current-savings':10000,
        'yearly-contribution':1200,
        'expected-return':7,
        'duration':10
    });

    const handleSubmit=(e)=>{
        e.preventDefault();
        console.log('Submit')
    }

    const resetHandler=(e)=>{
        console.log('Reset')
    }

    const changeHandler=(input,value)=>{
        console.log(input,value);
        setUserInput((prevInput)=>{
            return {
                ...prevInput
            }
        })
    }


    return(
    <form onSubmit={handleSubmit} className="form">
    <div className="input-group">
      <p>
        <label htmlFor="current-savings">Current Savings ($)</label>
        <input onChange={(e)=>changeHandler('current-savings',e.target.value)} type="number" id="current-savings" />
      </p>
      <p>
        <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
        <input onChange={(e)=>changeHandler('yearly-contribution',e.target.value)} type="number" id="yearly-contribution" />
      </p>
    </div>
    <div className="input-group">
      <p>
        <label htmlFor="expected-return">
          Expected Interest (%, per year)
        </label>
        <input onChange={(e)=>changeHandler('expected-return',e.target.value)} type="number" id="expected-return" />
      </p>
      <p>
        <label htmlFor="duration">Investment Duration (years)</label>
        <input onChange={(e)=>changeHandler('duration',e.target.value)} type="number" id="duration" />
      </p>
    </div>
    <p className="actions">
      <button type="reset" onClick={resetHandler} className="buttonAlt">
        Reset
      </button>
      <button type="submit" className="button">
        Calculate
      </button>
    </p>
  </form>
    )
}

export default UserInput
